import React from 'react'
import '../App.css'
import Image from 'react-graceful-image'
import axios from 'axios'


class UserProfilePage extends React.Component {
  state = {
    userId: null,
    userImages: [],
    userName: null,
    users: []
  }

  componentDidMount() {
    const userId = this.props.match.params.id;

    function getUserName() {
        const url = 'https://insta.nextacademy.com/users';
        return axios.get(url);
    }

    function getUserPhotos(userId) {
        const url = 'https://insta.nextacademy.com/api/v1/images?userId=' + userId;
        return axios.get(url);
    }


    axios.all([getUserName(), getUserPhotos()])
        .then((users, photos) => {
            // If successful, load users array with profile data
            this.setState({
                users: users.data,
                userImages: photos.data,
                isLoading: false
            })
        })
        .catch(error => {
            // If unsuccessful, console log error
            console.log('ERROR: ', error)
        })
    this.setState({
        userId: userId,
    });
  }

  render() {
      const { userId, userImages } = this.state;
    return (
        <div>
            <h1>User Profile Page</h1>
            {
                userImages.map(image =>
                    <Image src={image} />
                )
            }
        </div>
    )
  }
}

export default UserProfilePage
