import React from 'react'
import '../App.css'
import Image from 'react-graceful-image'
import axios from 'axios'


export default class UserProfilePage extends React.Component {
  state = {
    userId: null,
    userImages: [],
    userName: null,
    users: []
  }

  componentDidMount() {
    const userId = this.props.match.params.id;
    console.log(userId);

    function getUserName() {
        const url = 'https://insta.nextacademy.com/api/v1/users';
        return axios.get(url, {headers: {"Access-Control-Allow-Origin": "*"}});
    }

    function getUserPhotos(userId) {
        const url = `https://insta.nextacademy.com/api/v1/images?userId=${userId}`;
        return axios.get(url, {headers: {"Access-Control-Allow-Origin": "*"}});
    }
    axios.all([getUserName(), getUserPhotos(userId)])
        .then(axios.spread((users, photos) => {
            // If successful, load users array with profile data
            console.log('users' + users);
            console.log('photos' + photos);
            // this.setState({
            //     users: users.data,
            //     userImages: photos.data,
            //     isLoading: false
            // })
        }))
        .catch(error => {
            // If unsuccessful, console log error
            console.log('ERROR: ', error)
        }
        )
    }

  render() {

    return (
        <div>
            <h1>User Profile Page</h1>
            {/* {
                userImages.map(image =>
                    <Image src={image} />
                )
            } */}
        </div>
    )
  }
}
