import React from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'

export default class Register extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      show: true
    };
  }

  handleClose = () => {
    this.props.onHide();
  }

  handleFormSwitch = () => {
    this.props.onHide();
    console.log(this.props);
    this.props.show = true;
    this.props.isLogin = 
    this.setState({
        show: false,
        isLogin: true,
        isRegister: false
    })
  }

  render() {
      return(
      <Modal show={this.props.show} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Register for an Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formUsername">
                <Form.Label>Username</Form.Label>
                <Form.Control type="text" placeholder="Enter username" />
                <Form.Text className="text-muted">
                Choose from A-Z (uppercase/lowercase) and 0-9 characters only.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Enter password" />
            </Form.Group>
            <Form.Group controlId="formBasicChecbox">
                <Form.Check type="checkbox" label="Agree to Terms and Conditions" />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button className="btn-login" onClick={this.handleFormSwitch}>
            Login
          </Button>
          <Button className="btn-nextagram" onClick={this.handleClose}>
            Register
          </Button>
        </Modal.Footer>
      </Modal>

    )
  }
}
