import React from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Col from 'react-bootstrap/Col'
import InputGroup from 'react-bootstrap/InputGroup'

export default class Login extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
        show: true,
        validated: false,
        email: '',
        password: ''
    };
  }

    handleClose = () => {
        this.props.onHide();
    }

    handleFormSwitch = () => {
      this.props.onHide();
      this.props.isRegister();
    }


    handleInput = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const form = event.currentTarget;

        if (form.checkValidity() === false) {
            event.stopPropagation();
        }

        this.setState({
            validated: true
        });
        this.props.onHide();
        console.log(`Registration received from ${this.state.email} under username @${this.state.username}. The password is ${this.state.password}.`);
    }

  render() {
      const { validated } = this.state;
      return(
      <Modal show={this.props.show} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form
                noValidate
                validated={validated}
                onSubmit={this.handleSubmit}
            >
                <Form.Row>
                    <Form.Group as={Col} controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email Address" value={this.state.email} onChange={this.handleInput} required />
                        <Form.Control.Feedback type="invalid">
                        Please provide a valid email address.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                <Form.Group as={Col} controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={this.handleInput} required />
                    <Form.Control.Feedback type="invalid">
                    Please provide a valid password.
                    </Form.Control.Feedback>
                </Form.Group>
                </Form.Row>
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button className="btn-login" onClick={this.handleFormSwitch}>
            Register
          </Button>
          <Button className="btn-nextagram" type="submit" onClick={this.handleSubmit}>
            Login
          </Button>
        </Modal.Footer>
      </Modal>

    )
  }
}
