import React from 'react'
import '../App.css'
import Image from 'react-graceful-image'
import axios from 'axios'


export default class UserProfilePage extends React.Component {
  state = {
    userId: null,
    userImages: [],
    userName: null,
    users: []
  }

  componentDidMount() {
    const userId = this.props.match.params.id;

    axios.get('https://insta.nextacademy.com/api/v1/users/')
        .then(users => {
            // If successful, load users array with profile data
            const userName = (users.data.filter(user => user.id === userId)).username;
            console.log(userName);

        })
        .then(
            axios.get('https://insta.nextacademy.com/api/v1/images?userId=' + userId)
            .then(photos => {
                this.setState({
                    userImages: photos.data,
                })
            })
        )
        .catch(error => {
            // If unsuccessful, console log error
            console.log('ERROR: ', error)
        })
    }

  render() {
    const userImages = this.state.userImages;
    return (
        <div>
            <h1>User Profile Page</h1>
            <Row>

            </Row>
            <Row>
                {
                    userImages.map(image =>
                        <Col sm={4} className="mb-2">
                            <Image src={image} className="user-images" />
                        </Col>
                    )
                }
            </Row>
        </div>
    )
  }
}
