import React from 'react'
import Image from 'react-graceful-image'
import axios from 'axios'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import Loader from '../components/Loader'


export default class MyProfilePage extends React.Component {
  state = {
    userImages: []
  }

    componentDidMount() {
        const token = localStorage.getItem('token');

        // GET request to obtain all images for current logged in user
        // Requires Authorization bearer token to obtain current user
        axios({
            method: 'get',
            url: 'https://insta.nextacademy.com/api/v1/images/me',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => {
            // Returns array of image urls strings for logged in user
            console.log('Success');
            console.log(response);
        })
        .catch(error => {
            // On failure, display error
            console.log(error);
        })
    }

  render() {

    return (
        <Container className="p-4" fluid>
            <Row className="my-5">
                {/* <Col sm={3}>
                    <Image src={userProfilePhoto} className="profile-image" alt={`Profile photo of ${userName}`} />
                </Col>
                <Col>
                    <h2 className="mt-3">@{userName}</h2>
                </Col> */}
            </Row>
            <Row>
                {/* {
                    isLoading ? 
                        <Loader size={200}/>
                    : userImages.map(image =>
                        <Col sm={4} className="mb-2">
                            <Image src={image} className="user-images" alt={`Image from ${userName}`} />
                        </Col>
                    )
                } */}
            </Row>
        </Container>
    )
  }
}
