import React from 'react'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Col from 'react-bootstrap/Col'
import InputGroup from 'react-bootstrap/InputGroup'

export default class NameForm extends React.Component {
    constructor(...args) {
        super(...args);

        this.state = { 
            validated: false,
            name: '',
            email: '',
            username: '',
            password: '',
            confirmPassword: ''
            };
    }

    handleInput = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        })
    }

    handlePasswordValidation = (event) => {
        const password = this.state.password;
        const confirmPassword = this.state.confirmPassword;

        if(password != confirmPassword) {
            confirm_password.setCustomValidity("Passwords Don't Match");
        } else {
            confirm_password.setCustomValidity('');
            this.handleSubmit(event);
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();

        const form = event.currentTarget;

        if (form.checkValidity() === false) {
            event.stopPropagation();
        }

        this.setState({ validated: true });

        console.log(`Registration received from ${this.state.email} under username @${this.state.username}. The password is ${this.state.password}.`);

    }

    render() {
    const { validated } = this.state;

    return (
        <Container className="mt-5 pt-5">
            <Form
                noValidate
                validated={validated}
                onSubmit={this.handleSubmit}
            >
                <Form.Row>
                    <Form.Group as={Col} md="6" controlId="username">
                        <Form.Label>Username</Form.Label>
                        <InputGroup>
                        <InputGroup.Prepend>
                            <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                        </InputGroup.Prepend>
                        <Form.Control
                            type="text"
                            placeholder="Username"
                            aria-describedby="inputGroupPrepend"
                            value={this.state.username} onChange={this.handleInput}
                            required
                        />
                        <Form.Control.Feedback type="invalid">
                            Please choose a username.
                        </Form.Control.Feedback>
                        </InputGroup>
                    </Form.Group>
                    <Form.Group as={Col} md="6" controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email Address" value={this.state.email} onChange={this.handleInput} required />
                        <Form.Control.Feedback type="invalid">
                        Please provide a valid email address.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                <Form.Group as={Col} md="6" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={this.handleInput} required />
                    <Form.Control.Feedback type="invalid">
                    Please provide a valid password.
                    </Form.Control.Feedback>
                </Form.Group>
                <Form.Group as={Col} md="6" controlId="confirmPassword">
                    <Form.Label>Re-enter Password to Confirm</Form.Label>
                    <Form.Control  type="password" placeholder="Confirm Password" value={this.state.confirmPassword} onChange={this.handleInput} required />
                    <Form.Control.Feedback type="invalid">
                    Please make sure your password matches.
                    </Form.Control.Feedback>
                </Form.Group>
                </Form.Row>
                <Form.Group>
                <Form.Check
                    required
                    label="Agree to terms and conditions"
                    feedback="You must agree before submitting."
                />
                </Form.Group>
                <Button type="submit" onClick={this.handlePasswordValidation}>Register</Button>
            </Form>
        </Container>
    );
  }
}
