import React from 'react'
import './App.css'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Image from 'react-bootstrap/Image'
import axios from 'axios'
import Loader from './images/spinner.gif';

class UserImages extends React.Component {
  state = {
    userImages: [],
    isLoading: true,
    userId: null
  }

  componentDidMount() {
      const userId = this.props.userId;
    // GET request to Nextagram API
    axios.get('https://insta.nextacademy.com/api/v1/images?userId=' + userId)
    .then(result => {
      // If successful, load users array with profile data
      this.setState({
        userImages: result.data,
        isLoading: false
      })
    })
    .catch(error => {
      // If unsuccessful, console log error
      console.log('ERROR: ', error)
    })
  }

  render(){
    return (
        <Container>
            {
                this.state.isLoading ? 
                <Row>
                    Loading...
                </Row>
                : 
                this.state.userImages.map(image =>
                    <Image src={image} className="user-images" />
                )
            }
        </Container>
    )
  }
}

export default class HomePage extends React.Component {
  state = {
    users: [],
    isLoading: true
  }

  componentDidMount() {
    // GET request to Nextagram API
    axios.get('https://insta.nextacademy.com/api/v1/users')
    .then(result => {
      // If successful, load users array with profile data
      this.setState({
        users: result.data,
        isLoading: false
      })
    })
    .catch(error => {
      // If unsuccessful, console log error
      console.log('ERROR: ', error)
    })
  }

  render(){
    return (
      <Container className="p-2">
        <Row className="p-4">
          <h1>Nextagram</h1>
          <ul>
            {
              this.state.isLoading ? 
                <Row>
                  <img src={Loader} />
                </Row>
              : 
              (
                this.state.users.map(user =>
                  <li key={user.id}>
                    <Row>
                      {user.username} <br />
                      <Col>
                        <Image src={user.profileImage} className="profile-image" roundedCircle fluid />
                      </Col>
                      <Col>
                        <UserImages userId={user.id}/>
                      </Col>
                    </Row>
                  </li>
                )
              )
            }
          </ul>
        </Row>
      </Container>
    )
  }
}
