import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Image from 'react-graceful-image'
import Button from 'react-bootstrap/Button'
import axios from 'axios'
import Loader from '../components/Loader'
import UserImages from '../containers/UserImages'
import {Link} from 'react-router-dom'

export default class HomePage extends React.Component {
  state = {
    users: [],
    isLoading: true
  }

  componentDidMount() {
    // GET request to Nextagram API
    axios.get('https://insta.nextacademy.com/api/v1/users')
    .then(result => {
      // If successful, load users array with profile data
      this.setState({
        users: result.data,
        isLoading: false
      })
    })
    .catch(error => {
      // If unsuccessful, console log error
      console.log('ERROR: ', error)
    })
  }

  render(){
    return (
      <Container className="mt-5" fluid>
            {
              this.state.isLoading ? 
                <Loader size={200}/>
              : 
              (
                this.state.users.map(user =>
                  <Row className="rounded pt-3 pb-3 user-background" key={user.id}>
                      <Col sm={2} className="mb-4">
                          <Link to={`/users/${user.id}`} className="font-weight-bold username">{user.username}</Link>
                          <Image src={user.profileImage} className="profile-image mt-2 mb-4" alt="" /><br />
                          <Link to={`/users/${user.id}`} className="font-weight-bold username"><Button className="btn-nextagram btn-block">See More</Button></Link>
                      </Col>
                      <Col>
                          <UserImages userId={user.id}/>
                      </Col>
                  </Row>
                )
              )
            }
      </Container>
    )
  }
}
