import React from 'react'
import '../App.css'
import Navbar from 'react-bootstrap/Navbar'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import FormControl from 'react-bootstrap/FormControl'
import Nav from 'react-bootstrap/Nav'
import Logo from '../images/icons8_instagram.png'
import Login from '../components/Login'
import Register from '../components/Register'


export default class NavHeader extends React.Component{
    constructor(props, context) {
        super(props, context);
        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.state = {
            show: false,
            isRegister: false
        };
    }
    handleClose(state) {
        this.setState({
            show: state
        });
    }

    handleShow(form) {

        form == 'login' ?
        this.setState({
            show: true
        })
        :
        this.setState({
            isRegister: true
        })
    }

    render(){
        return(
            <div>
                <Navbar collapseOnSelect expand="lg" bg="light" variant="light" fixed="top">
                    <Navbar.Brand href="/">
                    <img
                        alt="Nextagram logo"
                        src={Logo}
                        width="35"
                        height="35"
                        className="d-inline-block mr-2"
                    />
                    <span>Nextagram</span></Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="/">Home</Nav.Link>
                            <Nav.Link href="/users/1">Profile</Nav.Link>
                        </Nav>
                        <Form inline>
                            <FormControl type="text" placeholder="Search for users" className="mr-sm-2" />
                            <Button variant="outline-primary" className="mr-2 btn-login" onClick={() => this.handleShow('login')}>Login</Button>
                            <Button className="btn-nextagram mr-2 btn-login" onClick={() => this.handleShow('register')}>Register</Button>
                        </Form>
                    </Navbar.Collapse>
                </Navbar>
                <Login show={this.state.show} onHide={this.handleClose}/>
                <Register show={this.state.isRegister} onHide={this.handleClose}/>
            </div>
        )
    }
}
