import React from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'

export default class Login extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      show: true,
      email: '',
      password: '',
    };
  }

  handleClose = () => {
    this.props.onHide();
  }

  handleFormSwitch = () => {
    this.props.onHide();
    this.props.isRegister();
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log('Login from ' + this.state.email + ' with password ' + this.state.password);
  }

  handleInput  = (event) => {
    this.setState({
      [event.target.type]: event.target.value
    })
  }

  render() {

      return(

      <Modal show={this.props.show} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={this.handleSubmit}>
            <Form.Group controlId="loginEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" value={this.state.email} onChange={this.handleInput} />
            </Form.Group>
            <Form.Group controlId="loginPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={this.handleInput} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button className="btn-login" onClick={this.handleFormSwitch}>
            Register
          </Button>
          <Button className="btn-nextagram" onClick={this.handleSubmit}>
            Login
          </Button>
        </Modal.Footer>
      </Modal>

    )
  }
}
